import GameConfig from '../GameConfig';
import { useState } from 'react';

export default function GameConfigExample() {
  const [config, setConfig] = useState({
    gameMode: "501" as "501" | "301" | "701" | "around_the_clock",
    startingScore: "501" as "501" | "301" | "701",
    numberOfLegs: 2,
    numberOfSets: 1,
    inMode: "standard" as "standard" | "double" | "master",
    outMode: "standard" as "standard" | "double" | "master",
  });

  return (
    <GameConfig
      gameMode={config.gameMode}
      startingScore={config.startingScore}
      numberOfLegs={config.numberOfLegs}
      numberOfSets={config.numberOfSets}
      inMode={config.inMode}
      outMode={config.outMode}
      onGameModeChange={(mode) => setConfig({ ...config, gameMode: mode, startingScore: mode !== "around_the_clock" ? mode : "501" })}
      onStartingScoreChange={(score) => setConfig({ ...config, startingScore: score })}
      onNumberOfLegsChange={(legs) => setConfig({ ...config, numberOfLegs: legs })}
      onNumberOfSetsChange={(sets) => setConfig({ ...config, numberOfSets: sets })}
      onInModeChange={(mode) => setConfig({ ...config, inMode: mode })}
      onOutModeChange={(mode) => setConfig({ ...config, outMode: mode })}
    />
  );
}

shared/schema.ts.content:
import { z } from "zod";

// Player type for the game
export const playerSchema = z.object({
  id: z.string(),
  name: z.string(),
  score: z.number(),
  throws: z.array(z.number()),
  currentThrowScores: z.array(z.number()),
  legs: z.number(),
  sets: z.number(),
  totalThrows: z.number(),
  totalScore: z.number(),
  hasStarted: z.boolean(),
});

export type Player = z.infer<typeof playerSchema>;

// Game configuration
export const gameConfigSchema = z.object({
  gameMode: z.enum(["501", "301", "701", "around_the_clock"]),
  startingScore: z.enum(["301", "501", "701"]),
  numberOfLegs: z.number().min(1),
  numberOfSets: z.number().min(1),
  inMode: z.enum(["standard", "double", "master"]),
  outMode: z.enum(["standard", "double", "master"]),
});

export type GameConfig = z.infer<typeof gameConfigSchema>;

// Game state
export const gameStateSchema = z.object({
  isStarted: z.boolean(),
  currentPlayerIndex: z.number(),
  currentDartIndex: z.number(),
  isDoubleMode: z.boolean(),
  isTripleMode: z.boolean(),
  players: z.array(playerSchema),
  config: gameConfigSchema,
  throwHistory: z.array(z.object({
    playerId: z.string(),
    playerName: z.string(),
    scores: z.array(z.number()),
    total: z.number(),
  })),
});

export type GameState = z.infer<typeof gameStateSchema>;

server/app.ts.content:
import { type Server } from "node:http";

import express, {
  type Express,
  type Request,
  Response,
  NextFunction,
} from "express";

import { registerRoutes } from "./routes";

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export const app = express();

declare module 'http' {
  interface IncomingMessage {
    rawBody: unknown
  }
}
app.use(express.json({
  verify: (req, _res, buf) => {
    req.rawBody = buf;
  }
}));
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "‚Ä¶";
      }

      log(logLine);
    }
  });

  next();
});

export default async function runApp(
  setup: (app: Express, server: Server) => Promise<void>,
) {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly run the final setup after setting up all the other routes so
  // the catch-all route doesn't interfere with the other routes
  await setup(app, server);

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
}

